const request = require('request');

const options = {
  method: 'POST',
  url: 'http://localhost:4585/em/api/v1/account',
  headers: {'content-type': 'application/json'},
  body: {email: 'me@mymail.com', password: 'password', accountType: 'candidate'},
  json: true
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

